import React from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import SideBar from './components/SideBar';
import Header from './components/Header';
import Body from './components/Body';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component{
  constructor(props){ //need to have super(props) for constructor
    super(props); //super just calls React.Component

    this.state = {
      articleItems:[], // Items from Database
      selectedName: '', // Item name that is selected from SideBar
      selectedBody: ''
    };
    this.onSelectedName = this.onSelectedName.bind(this);
    this.onSelectedBody = this.onSelectedBody.bind(this);
  }


  onSelectedName(selectedName){
    console.log(selectedName)
    this.setState({selectedName});
  }

  onSelectedBody(selectedBody){
    console.log(selectedBody)
    this.setState({selectedBody});
  }


  componentDidMount(){  //get info from Database
    const url = 'http://localhost:5000/items';
    axios.get(url).then(response => {
      const articleItems = response.data.items;
      this.setState({articleItems: articleItems})
      /*for (var i = 0; i < this.articleItems.length; ++i){
        console.log(this.articleItems[i].name + " ...");
      }*/
    })
    .catch(err => console.log(err))
  }

   render(){
    return (
      <Container fluid style={{minHeight:"100%"}}>
         <Row style={{minHeight:"100%"}}>
          <SideBar articleItems={this.state.articleItems} onSelect={this.onSelectedName}/>
          <Col md="10">
            <Header headerName={this.state.selectedName} />
            <br/>
            <Body bodyInfo={this.state.selectedBody} />
          </Col>
         </Row>
      </Container>
    );
  }
