import React from 'react'
import axios from 'axios'

export default class Form extends React.Component{
  constructor(props){
    super(props)

    this.state ={
      name: '',
      info: '',
      category: '',
    }
  }

  changeHandler = (e) => {
    this.setState({[e.target.name]: e.target.value})
  }

  submitHandler = (e) => {
    e.preventDefault()
    console.log(this.state)
    axios.post('http://localhost:5000/items')
    //TODO - https://www.youtube.com/watch?v=x9UEDRbLhJE - 4:30
  }

  render(){
    const {name, info, category} = this.state
    //POST needs a server - <form method="POST">
    return(
      <form onSubmit={this.submitHandler}>
        <input type="text" placeholder="name" name="name"
        value={name} onChange={this.changeHandler} />
        &nbsp;
        <input type="text" placeholder="info" name="info"
        value={info} onChange={this.changeHandler} />
        &nbsp;
        <input type="text" placeholder="category" name="category"
        value={category} onChange={this.changeHandler}/>
        &nbsp;
        <button type="submit"> Submit </button>
      </form>
    )
   }
}
