  postForm(){
    const {name, info, category_id} = this.state
    return (
      <form onSubmit={this.props.onPost}>
       <h5> POST </h5>
         <input type="text" placeholder="name" name="name"
          value={name} onChange={this.props.onPostChange} required />
         &nbsp;
         <input type="text" placeholder="info" name="info"
          value={info} onChange={this.props.onPostChange} />
         &nbsp;
         <input type="number" placeholder="category ID" name="category_id"
          value={category_id} onChange={this.props.onPostChange} required />
         &nbsp;
         <button type="submit"> Post </button>
      </form>
      )
  }

  putForm(){
   const {name, info, category_id} = this.state
   return(
     <form onSubmit={this.props.onPut}>
     <h5> PUT </h5>
       <input type="text" placeholder="name" name="name"
       value={name} onChange={this.props.onPutChange} required />
       &nbsp;
       <input type="text" placeholder="info" name="info"
       value={info} onChange={this.props.onPutChange} />
       &nbsp;
       <input type="number" placeholder="category ID" name="category_id"
       value={category_id} onChange={this.props.onPutChange} />
       &nbsp;
       <button type="submit"> Put </button>
     </form>
   )
  }

  deleteForm(){
    const {name, info, category_id} = this.state
    return(<form onSubmit={this.props.onDelete}>
