       <button type="submit"> Delete </button>
     </form>
   )
  }

  switchFormBody = () => {
    var formBody = null;
    switch (this.props.formToShow) {
      case "post":
        formBody = this.postForm()
        break;
      case "put":
        formBody = this.putForm()
        break;
      case "delete":
        formBody = this.deleteForm()
        break;
      default:
        return null;
    }
    return formBody;
  }

  render(){
    return(
      <div>
      { this.switchFormBody() }
      </div>
    )
   }
}
