import React from 'react'
import axios from 'axios'

export default class Form extends React.Component{
  constructor(props){
    super(props)

    this.state = {
      name: '',
      info: '',
      category_id: '',
      //artItems: this.props.articleItems
    }
  }

  changeHandler = (e) => {
    this.setState({[e.target.name]: e.target.value})
  }

  postSubmitHandler = (e) => {
    e.preventDefault()
    console.log(this.state) // outputs the state of the computer
    const postItem = this.state.name
    return axios.post('http://localhost:5000/item/${postItem}', this.state)
      .then(response => {
         //TODO - need to append to array here to refresh automatically
         // probably need to map the id and then append it. - see Sidebar.js
         //https://stackoverflow.com/questions/49516309/reactjs-setstate-axios-post-response
      })
      .catch(error => {
         console.log(error)
      })
  }

  deleteSubmitHandler = (e) => {
    e.preventDefault()
    console.log(this.state)
    const deleteItem = this.state.name
    return axios.delete('http://localhost:5000/item/${deleteItem}', this.state)
      .catch(error => {
        console.log(error)
      })
  }

  render(){
    const {name, info, category_id} = this.state
    console.log(this.artItems) //UNDEF
    return(
      <div>
       <form onSubmit={this.postSubmitHandler}>
        <h5> POST </h5>
          <input type="text" placeholder="name" name="name"
          value={name} onChange={this.changeHandler} required />
          &nbsp;
          <input type="text" placeholder="info" name="info"
          value={info} onChange={this.changeHandler} />
          &nbsp;
          <input type="number" placeholder="category ID" name="category_id"
          value={category_id} onChange={this.changeHandler} required />
          &nbsp;
          <button type="submit"> Submit </button>
       </form>
       <br/>
       <form onSubmit={this.deleteSubmitHandler}> 
          <h5> DELETE </h5>
            <input type="text" placeholder="name" name="name"
            value={name} onChange={this.changeHandler}/>
            <button type="submit"> Submit </button>
        </form>
        <br/>
       <form>
          <h5> PUT </h5>
            <input type="text" placeholder="name" name="name" />
            <button type="submit"> Submit </button>
       </form>


      </div>


    )
   }
}
