import React from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import SideBar from './components/SideBar';
import Header from './components/Header';
import Body from './components/Body';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component{
  constructor(props){
    super(props);
  }
  /* componentDidMount(){  //get info from Database
    const url = 'http://localhost:5000/items';
    axios.get(url).then(response => {
          this.articleItems = response.data.items;
          console.log(this.articleItems);
    })
  }
  render(){
    const articleItems = this.props.articleItems;
    return (
      <Container fluid style={{minHeight:"100%"}}>
         <Row style={{minHeight:"100%"}}>
          <SideBar name={this.articleItems} />
          <Col md="10">
            <Header />
            <br/>
            <Body />
          </Col>
         </Row>
      </Container>
    );
  }
  */
  state = {
    articleItems:[],
  };
  componentDidMount(){  //get info from Database
    const url = 'http://localhost:5000/items';
    axios.get(url).then(response => {
      console.log(response);
      this.setState({ articleItems: response.data })
    })
  }
  render(){
    return(
      <ul>
      {this.state.articleItems.map(articleItem =>
      <li>
      {articleItem.name}
      </li>
      )}
      </ul>
    )
  }
}



