import React from 'react'
import { Col } from 'react-bootstrap';

export default class SideBar extends React.Component{
  constructor(props){
    super(props);
  }

  componentWillReceiveProps(nextProps) {
      if ( !this.props.shouldFetch && nextProps.shouldFetch ) {
        // Fetch data here and set it in state, for example
        // After fetch, don't forget to call onFetch() to reset the boolean
      }
  }
  selectItem = (id) => {
    //console.log(name)
    this.props.onSelect(id)
  }

  render(){
   return (
    <Col md="2" style={{backgroundColor:"#3C3C3C", color:"#FFFFFF", height:"100vh"}}>
    <br/>
    <h5>Sidebar</h5>
      <ul style={{marginTop:"10px"}}>
       { this.props.articleItems.map(articleItem => (
         <li onClick={() => this.selectItem(articleItem.id)} key={articleItem.id}>
          {articleItem.name}
         </li>))}
    </ul>
    </Col>
   )
  }
}
