import React from 'react'
import { Col } from 'react-bootstrap';

export default class SideBar extends React.Component{

  selectItem(){
    const {selectedItem} = this.setState;
    const {onSelect} = this.props;

    onSelect(selectedItem)
  }

  render(){
   return (
    <Col md="2" style={{backgroundColor:"#3C3C3C", color:"#FFFFFF", height:"100vh"}}>
    <h5>Sidebar</h5>
      <ul onClick={this.selectItem} style={{marginTop:"10px"}}>
      { this.props.articleItems.map(articleItem => <li>{articleItem.name}</li>)}
    </ul>
    </Col>
   )
  }
}
